using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.Networking;
using UnityEngine.UI;

public class Network_Test : MonoBehaviour
{
    // Start is called before the first frame update
    void Start()
    {
        StartCoroutine(TextureLoad());
    }

    #region Setver 통신
    IEnumerator UnityWebRequestGet()
    {
        string jobID = "41f1cdc2ff58bb5fdc287be0db2a8df3"; // 귀검사
        string jobGrowId  = "df3870efe8e8754011cd12fa03cd275f"; // 웨폰마스터

        // 넥슨의 던전앤파이터 직업 API
        string JobListurl = "https://api.neople.co.kr/df/jobs?apikey=hr63tfE6WGawf0pHVKryTYmVubIGGuwg";
        string JobSkillList = $"https://api.neople.co.kr/df/skills/{jobID}?jobGrowId={jobGrowId}&apikey=hr63tfE6WGawf0pHVKryTYmVubIGGuwg";

        // 요청하기
        UnityWebRequest www = UnityWebRequest.Get(JobSkillList);


        // 응답 기다리기
        yield return www.SendWebRequest();


        if (www.error == null)
        {
            Debug.Log(www.downloadHandler.text);
        }
        else
        {
            Debug.Log("ERROR");
        }
    }

    IEnumerator WWW()
    {
        string jobID = "41f1cdc2ff58bb5fdc287be0db2a8df3"; // 귀검사
        string jobGrowId  = "df3870efe8e8754011cd12fa03cd275f"; // 웨폰마스터

        // 넥슨의 던전앤파이터 직업 API
        string JobListurl = "https://api.neople.co.kr/df/jobs?apikey=hr63tfE6WGawf0pHVKryTYmVubIGGuwg";
        string JobSkillList = $"https://api.neople.co.kr/df/skills/{jobID}?jobGrowId={jobGrowId}&apikey=hr63tfE6WGawf0pHVKryTYmVubIGGuwg";

        // 요청하기

        // Request
        WWW www = new WWW(JobSkillList);

        // 응답 기다리기
        yield return www;

        if (www.error == null)
        {
            Debug.Log(www.text);
        }
        else
        {
            Debug.Log("ERROR");
        }
    }

    public RawImage _image;
    IEnumerator TextureLoad()
    {
        string severURL = "data:image/jpeg;base64,"; // 서버 주소
        UnityWebRequest www = UnityWebRequestTexture.GetTexture(severURL);

        yield return www.SendWebRequest();

        if(www.result != UnityWebRequest.Result.Success)
        {
            Debug.LogError(www.error);
        }
        else
        {
            _image.texture = ((DownloadHandlerTexture)www.downloadHandler).texture;
        }
    }

    #endregion
}
